import * as Sentry from "@sentry/node";
import { EmbedBuilder, WebhookClient } from "discord.js";

import { logHandler } from "./logHandler";

/**
 * Standard error handling module to pipe errors to Sentry and
 * format the error for logging.
 *
 * @param {string} context A description of where the error occurred.
 * @param {unknown} err The error object.
 */
export const errorHandler = async (
  context: string,
  err: unknown
): Promise<void> => {
  const error = err as Error;
  logHandler.log("error", `There was an error in the ${context}:`);
  logHandler.log(
    "error",
    JSON.stringify({ errorMessage: error.message, errorStack: error.stack })
  );
  Sentry.captureException(error);

  const hook = new WebhookClient({ url: process.env.DEBUG_HOOK as string });

  const embed = new EmbedBuilder();
  embed.setTitle(`There was an error in the ${context}`);
  embed.setDescription(error.message.slice(0, 2000));
  embed.addFields([
    {
      name: "Stack",
      value: `\`\`\`${error.stack?.slice(0, 1000) || "no stack"}\`\`\``,
    },
  ]);

  await hook.send({ embeds: [embed] });
};

import { createLogger, format, transports, config } from "winston";

const { combine, timestamp, colorize, printf } = format;

/**
 * Standard log handler, using winston to wrap and format
 * messages. Call with `logHandler.log(level, message)`.
 *
 * @param {string} level - The log level to use.
 * @param {string} message - The message to log.
 */
export const logHandler = createLogger({
  levels: config.npm.levels,
  level: "silly",
  transports: [new transports.Console()],
  format: combine(
    timestamp({
      format: "YYYY-MM-DD HH:mm:ss",
    }),
    colorize(),
    printf((info) => `${info.level}: ${[info.timestamp]}: ${info.message}`)
  ),
  exitOnError: false,
});

import { AttachmentBuilder, SlashCommandBuilder } from "discord.js";

import { Command } from "../interfaces/Command";
import { errorHandler } from "../utils/errorHandler";

export const holopin: Command = {
  data: new SlashCommandBuilder()
    .setName("holopin")
    .setDescription("Display a user's Holopin badge board.")
    .addStringOption((option) =>
      option
        .setName("username")
        .setDescription("The user to display the Holopin badge board for.")
        .setRequired(true)
        .setAutocomplete(true)
    ),
  run: async (_bot, interaction) => {
    try {
      await interaction.deferReply();
      const target = interaction.options.getString("username", true);

      const res = await fetch(`https://holopin.me/${target}`).catch(() => null);

      if (!res || res.status !== 200) {
        await interaction.editReply(
          "Could not locate that user's Holopin badge board."
        );
        return;
      }

      const arrayBuffer = await res.arrayBuffer();
      const buffer = Buffer.from(arrayBuffer);
      const attachment = new AttachmentBuilder(buffer, {
        name: `${target}.png`,
      });

      await interaction.editReply({ files: [attachment] });
    } catch (err) {
      await errorHandler("holopin command", err);
    }
  },
};

