import { Command } from "../interfaces/Command";

export const report: Command = {
  data: new SlashCommandBuilder()
    .setName("report")
    .setDescription(
      "Report a repository for being ineligible for Hacktoberfest."
    )
    .addStringOption((option) =>
      option
        .setName("provider")
        .setDescription("The VCS provider for the repository.")
        .setRequired(true)
        .setChoices(
          { name: "GitHub", value: "github" },
          { name: "GitLab", value: "gitlab" }
        )
    )
    .addStringOption((option) =>
      option
        .setName("owner")
        .setDescription("The username of the repository owner.")
        .setRequired(true)
    )
    .addStringOption((option) =>
      option
        .setName("repo")
        .setDescription("The name of the repository.")
        .setRequired(true)
    ),
  run: async (bot, interaction) => {
    await interaction.deferReply({ ephemeral: true });

    const provider = interaction.options.getString("provider", true);
    const owner = interaction.options.getString("owner", true);
    const repo = interaction.options.getString("repo", true);

    const user = interaction.user.id;
    const token = bot.reportTokens[user];

    if (!token) {
      await interaction.editReply(
        "You must set your token before reporting a repository. Please use `/token` to set your token."
      );
      return;
    }

    const response = await fetch(
      "https://hackathon-tracker.digitalocean.com/events/5/excluded_repositories",
      {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ provider, name: `${owner}/${repo}` }),
      }
    )
      .then(async (res) => await res.json())
      .catch((err) => err);

    if (response.code === "InvalidContent") {
      await interaction.editReply("You have already reported this repository.");
      return;
    }

    if (response.code === "InvalidCredentials") {
      await interaction.editReply(
        "Your token is invalid. Please use `/token` to update your token."
      );
      return;
    }

    if (response.code === "NotFound") {
      await interaction.editReply(
        `Could not find a repository at https://${provider}.com/${owner}/${repo}`
      );
      return;
    }

    await interaction.editReply(
      `Your report has been submitted. Thank you for your contribution!`
    );
  },
};

import { EmbedBuilder, SlashCommandBuilder } from "discord.js";

import {
  GitLabLanguageIds,
  LanguageChoices,
  LanguageColours,
} from "../config/Languages";
import { Command } from "../interfaces/Command";
import { errorHandler } from "../utils/errorHandler";

export const search: Command = {
  data: new SlashCommandBuilder()
    .setName("search")
    .setDescription(
      "Discover opensource projects built with your programming language."
    )
    .addStringOption((option) =>
      option
        .setName("vcs")
        .setDescription("The Version Control Service that you want to search.")
        .addChoices(
          { name: "GitHub", value: "github" },
          { name: "GitLab", value: "gitlab" }
        )
        .setRequired(true)
    )
    .addStringOption((options) =>
      options
        .setName("lang")
        .setDescription("The Language that you want to search for.")
        .setRequired(true)
        .setAutocomplete(true)
    ),
  run: async (_bot, interaction) => {
    try {
      await interaction.deferReply();
      const vcs = interaction.options.getString("vcs", true);
      const lang = interaction.options.getString(
        "lang",
        true
      ) as keyof typeof LanguageChoices;

      const url =
        vcs === "github"
          ? `https://${vcs}.com/search?q=topic%3Ahacktoberfest%20language%3A${lang}&type=repositories`
          : `https://${vcs}.com/explore/projects/topics/hacktoberfest?language=${
              GitLabLanguageIds[lang] ?? ""
            }`;

      const embed = new EmbedBuilder()
        .setTitle(`${lang} projects on ${vcs}`)
        .setDescription(
          `[Click here to view ${lang} projects on ${vcs}](${url})`
        )
        .setColor(LanguageColours[lang] ?? null);
      embed.setFooter({
        text: "Join our server: https://chat.nhcarrigan.com",
        iconURL: "https://cdn.nhcarrigan.com/profile.png",
      });
      await interaction.editReply({ embeds: [embed] });
    } catch (err) {
      await errorHandler("search command", err);
    }
  },
};

import { SlashCommandBuilder } from "discord.js";

import { Command } from "../interfaces/Command";

export const token: Command = {
  data: new SlashCommandBuilder()
    .setName("token")
    .setDescription("Set or update your reporting token.")
    .addStringOption((option) =>
      option
        .setName("token")
        .setDescription("Your Hacktoberfest auth token.")
        .setRequired(true)
    ),
  run: async (bot, interaction) => {
    await interaction.deferReply({ ephemeral: true });

    const token = interaction.options.getString("token", true);

    const user = interaction.user.id;

    const response = await fetch(
      "https://hackathon-tracker.digitalocean.com/users/%40me",
      {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      }
    )
      .then(async (res) => await res.json())
      .catch((err) => err);

    if (response.code === "InvalidCredentials") {
      await interaction.editReply("Your token is invalid.");
      return;
    }

    bot.reportTokens[user] = token;

    await interaction.editReply(
      `Your token has been set for the account ${response.name || "unknown"}`
    );
  },
};

