interface params {
  repo: string;
  owner: string;
  userId: string;
}

const threeDays = 1000 * 60 * 60 * 24 * 3;

/**
 * Checks if the database contains the specified project for the user.
 * Creates it if not.
 *
 * @param {Bot} client The bot's Discord instance.
 * @param {params} opts The userId, repo, and owner.
 * @returns {boolean} True if already present, false if not. Null on error.
 */
export const isInDatabase = async (
  client: Bot,
  opts: params
): Promise<boolean | null> => {
  try {
    const exists = await client.db.links.findUnique({
      where: {
        userId_repo_owner: opts,
      },
    });
    if (exists) {
      // return true only if exists and hasn't been three days since last sent
      if (exists.lastSent.getTime() + threeDays > Date.now()) {
        return true;
      }
      // need to update here, create call would fail because of unique index
      await client.db.links.update({
        where: {
          userId_repo_owner: opts,
        },
        data: {
          lastSent: new Date(),
        },
      });
      return false;
    }
    await client.db.links.create({
      data: opts,
    });
    return false;
  } catch (err) {
    await errorHandler("isInDatabase", err);
    return null;
  }
};

import {
  boundaryEnd,
  boundaryStart,
  optionalIssues,
} from "../config/ProjectRegex";

/**
 * Parses the repository and owner from a project link.
 *
 * @param {string} message The match content from the ProjectRegex.match call.
 * @returns {{ repo: string; owner: string }} The repository and owner.
 */
export const parseProjectLink = (
  message: string
): { repo: string; owner: string } => {
  const stripped = message
    .replace(new RegExp(`^${boundaryStart}`), "")
    .replace(new RegExp(`${boundaryEnd}$`), "")
    .replace(new RegExp(`${optionalIssues}$`), "")
    .replace(/\.$/, "")
    .replace(/\/$/, "");
  const split = stripped.split("/");
  const repo = split.pop();
  const owner = split.pop();
  return { repo: repo?.trim() || "", owner: owner?.trim() || "" };
};

