/**
 * Returns a random index from the given array of phrases.
 *
 * @param {string[]} phrases An array of sentences to choose from.
 * @returns {number} The random index.
 */
export const getRandom = <T>(phrases: Array<T>): T => {
  const result = phrases[Math.floor(Math.random() * phrases.length)];
  return result as T;
};

import { Message } from "discord.js";

import { LoveData } from "../interfaces/LoveData";
import { errorHandler } from "../utils/errorHandler";

import { checkMentions } from "./checkMentions";

/**
 * Universal module to check if the message meets the love
 * requirements.
 *
 * @param {Message} message The message to validate.
 * @param {LoveData} data The data to validate against.
 * @returns {boolean} True if message meets conditions.
 */
export const validateLove = (message: Message, data: LoveData): boolean => {
  try {
    return (
      message.content
        .replace(/\s/g, "")
        .toLowerCase()
        .includes(data.name.toLowerCase()) ||
      message.content.includes(data.emoji) ||
      (!!data.id &&
        (message.author.id === data.id || checkMentions(message, data.id)))
    );
  } catch (err) {
    void errorHandler("validate love", err);
    return false;
  }
};

